import React from "react"
import '../styles/main.css';
import { fade, makeStyles } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import CardContent from '@material-ui/core/CardContent';
import SearchIcon from '@material-ui/icons/Search';
import InputBase from '@material-ui/core/InputBase';
import { Button } from "@material-ui/core";

const useStyles = makeStyles((theme) => ({
    title: {
      display: 'none',
      [theme.breakpoints.down('sm')]: {
        display: 'block',
      },
    },
    search: {
      position: 'relative',
      borderRadius: theme.shape.borderRadius,
      backgroundColor: fade(theme.palette.common.black, 0.15),
      '&:hover': {
        backgroundColor: fade(theme.palette.common.black, 0.25),
      },
      marginRight: theme.spacing(2),
      marginLeft: 0,
      width: '100%',
      [theme.breakpoints.up('sm')]: {
        marginLeft: theme.spacing(3),
        width: 'auto',
      },
    },
    searchIcon: {
      padding: theme.spacing(0, 2),
      height: '100%',
      position: 'absolute',
      pointerEvents: 'none',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
    },
    inputRoot: {
      color: 'inherit',
      width: '100%',
    },
    inputInput: {
      padding: theme.spacing(1, 1, 1, 0),
      // vertical padding + font size from searchIcon
      paddingLeft: `calc(1em + ${theme.spacing(4)}px)`,
      transition: theme.transitions.create('width'),
      width: '100%',
      [theme.breakpoints.up('l')]: {
        width: '20ch',
      },
    },
  }));

const SearchRecipe = ({handleOnSearch}) => {
    const classes = useStyles()

    const handleSearch = (event) => {     
        console.log(event.nativeEvent.type)

        var charCode = event.key
        if(event.nativeEvent.type === "keypress" && charCode === "Enter")
            handleOnSearch(event.target.value)
        else if(event.nativeEvent.type === "click")
            handleOnSearch(document.getElementById("searchField").value)
    }

    return (
        <Card className="root">
            <CardContent>
                <p color="primary" className="title">Search for food here</p>  
                <div className={classes.search}>
                    <div className={classes.searchIcon}>
                        <SearchIcon />
                    </div>
                    <InputBase
                    id="searchField"
                    placeholder="Search…"
                    classes={{
                        root: classes.inputRoot,
                        input: classes.inputInput,
                    }}
                    inputProps={{ 'aria-label': 'search' }}
                    onKeyPress={handleSearch}
                    />                    
                </div>
                <Button id="searchButton" type="submit" onClick={handleSearch}>Analyse</Button>
            </CardContent>
        </Card>
    )
}

export default SearchRecipe
